mapper.delete:
DELETE FROM shuttle_common.sc_security_staff_role
WHERE staff_id=#{id}

    AND role_id IN

        #{item.id}

DELETE FROM shuttle_common.sc_security_staff_org
WHERE staff_id=#{id}

    AND organization_id IN

        #{item.id}

mapper.update:
UPDATE shuttle_common.sc_security_staff


        ,username=#{username}


        ,password=#{password}


        ,status=#{status}


        ,`name`=#{name}


        ,mobile=#{mobile}


        ,email=#{email}


        ,qq=#{qq}

    ,update_time=NOW()

WHERE id=#{id} AND superior=0
