mapper.delete:
DELETE FROM shuttle_common.sc_security_role_res
WHERE role_id=#{id}

    AND resource_id IN

        #{item.id}

mapper.update:
UPDATE shuttle_common.sc_security_role


        ,`name`=#{name}


        ,status=#{status}


        ,`desc`=#{desc}

    ,update_time=NOW()

WHERE id=#{id} AND superior=0

mapper.sql:
FROM shuttle_common.sc_security_role


        AND `name`=#{name}


        AND `name` LIKE CONCAT('%',#{name},'%')


        AND status=#{status}

    AND superior=0

mapper.select:
SELECT COUNT(*)
FROM shuttle_common.sc_security_staff_role
WHERE role_id=#{id}

SELECT COUNT(*)
FROM shuttle_common.sc_security_org_role
WHERE role_id=#{id}
