mapper.select:
SELECT IFNULL(SUM(amount), 0) AS amount, DATE_FORMAT(create_time, '%Y-%m-%d') AS label
FROM yq_order


        AND DATE(create_time) >= #{startTime}


        AND DATE(create_time) <= #{endTime}


GROUP BY label

SELECT SUM(amount) AS orderAmount, COUNT(id) AS orderCount FROM yq_order


        AND DATE(create_time) >= #{startTime}


        AND DATE(create_time) <= #{endTime}

SELECT SUM(amount) AS repayAmount FROM yq_payment


        AND DATE(refund_time) >= #{startTime}


        AND DATE(refund_time) <= #{endTime}
